import { FC, useState, useEffect } from 'react'
import { useDispatch } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { Form, Input, Button, Checkbox, Image } from 'antd'
import { MailOutlined, LockOutlined } from '@ant-design/icons'

import { UserType } from './../../types/user'

import AuthService from './../../services/user/auth.service'

import { setGlobalSpin } from './../../redux/reducers/spinSlice'

import './style.scss'

import { FormDataType, PropsType } from './type'

const NormalLoginForm: FC<PropsType> = (): JSX.Element => {
  const [loading, setLoading] = useState(false)
  const history: any = useHistory()
  const dispatch: any = useDispatch()

  const handleOnLoginSuccessfully: any = (user: UserType): void => {
    setLoading(false)
    dispatch(setGlobalSpin(true))
    history.push('/')
  }

  const onFinish: any = (values: FormDataType): void => {
    setLoading(true)
    handleOnLoginSuccessfully(values)

    if (values) {
      AuthService.login(values.email || '', values.password || '')
        .then((res: any): void => {
          handleOnLoginSuccessfully(values)
        })
        .catch((err: any): void => {
          setLoading(false)
        })
    }
  }

  const logout: any = (): void => {
    AuthService.logout()
  }

  useEffect((): void => {
    dispatch(setGlobalSpin(false))
    logout()
  }, [dispatch])

  return (
    <Form
      name="normal_login"
      className="login-page"
      initialValues={{
        email: 'a@a.com',
        password: '1',
        remember: true
      }}
      onFinish={onFinish}
    >
      <div className="login-page__logo">
        <Image
          width={200}
          height={200}
          src="error"
          fallback="data:image/png;base64,"
        />
      </div>

      <Form.Item
        name="email"
        rules={[
          {
            required: true,
            type: 'email',
            message: 'Please input valid Email!'
          }
        ]}
      >
        <Input
          prefix={<MailOutlined className="site-form-item-icon" />}
          placeholder="Email"
        />
      </Form.Item>

      <Form.Item
        name="password"
        rules={[
          {
            required: true,
            message: 'Please input your Password!'
          }
        ]}
      >
        <Input
          prefix={<LockOutlined className="site-form-item-icon" />}
          type="password"
          placeholder="Password"
        />
      </Form.Item>

      <Form.Item>
        <Form.Item name="remember" valuePropName="checked" noStyle={true}>
          <Checkbox>Remember me</Checkbox>
        </Form.Item>

        <a className="login-form-forgot" href="/">
          Forgot password
        </a>
      </Form.Item>

      <Form.Item>
        <Button
          type="primary"
          htmlType="submit"
          className="login-form-button"
          loading={loading}
        >
          Log in
        </Button>
        Or <a href="/register">register now!</a>
      </Form.Item>
    </Form>
  )
}

export default NormalLoginForm
